import React, { useState, useRef, useEffect } from 'react';
import { Input, Typography, Spin, message, Button } from 'antd';
import { UserOutlined, RobotOutlined, SendOutlined, DeleteOutlined, UploadOutlined } from '@ant-design/icons';
import axios from 'axios';

const { Text } = Typography;

interface Message {
  type: 'question' | 'answer';
  content: string;
  loading?: boolean;
}

const QAPage: React.FC = () => {
  const [inputValue, setInputValue] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [fileUploaded, setFileUploaded] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) {
      message.error('Please select a file to upload');
      return;
    }

    // Handle file upload (sending to FastAPI or backend service)
    const formData = new FormData();
    formData.append('file', file);

    try {
      // Assuming you have an endpoint for handling file uploads
      await axios.post('http://localhost:8000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setFileUploaded(true);
      message.success('File uploaded successfully!');
    } catch (error) {
      console.error('File upload error:', error);
      message.error('Failed to upload the file');
    }
  };

  const handleSend = async () => {
    if (!inputValue.trim() || isLoading) return;

    const question = inputValue;
    setInputValue('');

    setMessages(prev => [
      ...prev,
      { type: 'question', content: question },
      { type: 'answer', content: '', loading: true }
    ]);

    setIsLoading(true);

    try {
      await axios.post('http://localhost:8000/index');

      const response = await axios.post(
        'http://localhost:8000/ask',
        { question },
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

      setMessages(prev => {
        const newMessages = [...prev];
        newMessages[newMessages.length - 1] = {
          type: 'answer',
          content: response.data.answer || "Sorry, I couldn't find an answer.",
          loading: false
        };
        return newMessages;
      });
    } catch (error) {
      console.error('Error:', error);
      message.error('Failed to get answer');
      setMessages(prev => {
        const newMessages = [...prev];
        newMessages[newMessages.length - 1] = {
          type: 'answer',
          content: "Error: Couldn't connect to the AI service",
          loading: false
        };
        return newMessages;
      });
    } finally {
      setIsLoading(false);
    }
  };

  const clearMessages = () => {
    setMessages([]);
    message.info('Chat cleared');
  };

  return (
    <div style={{ padding: '24px', height: '100%', display: 'flex', flexDirection: 'column' }}>
      <Typography.Title level={3}>Chat with Document</Typography.Title>

      {/* File upload section */}
      <div style={{ marginBottom: 16 }}>
        <input
          type="file"
          onChange={handleFileUpload}
          style={{ display: 'none' }}
          id="file-upload"
        />
        <Button
          icon={<UploadOutlined />}
          onClick={() => document.getElementById('file-upload')?.click()}
          disabled={fileUploaded}
        >
          {fileUploaded ? 'File Uploaded' : 'Upload Document'}
        </Button>
      </div>

      {/* Clear chat button */}
      <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: 8 }}>
        <Button
          icon={<DeleteOutlined />}
          onClick={clearMessages}
          disabled={messages.length === 0}
          danger
        >
          Clear Chat
        </Button>
      </div>

      <div style={{ flex: 1, overflowY: 'auto', marginBottom: '16px', paddingRight: '8px' }}>
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'flex-start',
              marginBottom: 16,
            }}
          >
            <div
              style={{
                border: '1px solid #d9d9d9',
                borderRadius: 8,
                padding: '12px 16px',
                backgroundColor: msg.type === 'question' ? '#f5f5f5' : '#e6f7ff',
                width: 'fit-content',
                maxWidth: '80%',
              }}
            >
              {msg.loading ? (
                <Spin size="small" />
              ) : (
                <Text>
                  {msg.type === 'question' ? (
                    <UserOutlined style={{ color: '#1890ff' }} />
                  ) : (
                    <RobotOutlined style={{ color: '#52c41a' }} />
                  )}
                  <span style={{ marginLeft: 8 }}>{msg.content}</span>
                </Text>
              )}
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Question input and send button */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
        <Input
          value={inputValue}
          onChange={e => setInputValue(e.target.value)}
          onPressEnter={handleSend}
          placeholder="Ask a question about your documents..."
          disabled={isLoading || !fileUploaded} // Disable if no file uploaded
          style={{ flex: 1 }}
        />
        <SendOutlined
          onClick={handleSend}
          style={{
            fontSize: 22,
            color: isLoading || !fileUploaded ? '#d9d9d9' : '#1890ff',
            cursor: isLoading || !fileUploaded ? 'not-allowed' : 'pointer',
            transform: 'rotate(-45deg)',
            transition: 'all 0.2s',
          }}
          onMouseEnter={e =>
            !isLoading && fileUploaded && (e.currentTarget.style.transform = 'scale(1.2) rotate(-45deg)')
          }
          onMouseLeave={e =>
            e.currentTarget.style.transform = 'rotate(-45deg)'
          }
        />
      </div>
    </div>
  );
};

export default QAPage;
