// components/QAInterface.tsx
import React, { useState } from "react";
import { Input, Button, Spin, Alert, Typography } from "antd";
import { UserOutlined, RobotOutlined } from "@ant-design/icons";

const { Text } = Typography;

interface Message {
  type: "question" | "answer";
  text: string;
}

interface Props {
  loading: boolean;
  error: string;
  onSubmit: (question: string) => string;
}

const QAInterface: React.FC<Props> = ({ loading, error, onSubmit }) => {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState<Message[]>([]);

  const handleSend = () => {
    if (!input.trim()) return;

    const userMessage: Message = { type: "question", text: input };
    setMessages((prev) => [...prev, userMessage]);
    setInput("");

    const response = onSubmit(input);
    setTimeout(() => {
      setMessages((prev) => [...prev, { type: "answer", text: response }]);
    }, 500);
  };

  return (
    <div className="flex flex-col h-full bg-gray-50 rounded-lg border border-gray-200">
      {/* Chat history */}
      <div className="flex-1 overflow-y-auto p-6 space-y-8">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`flex flex-col ${
              msg.type === "question" ? "items-end" : "items-start"
            }`}
          >
            <div className="flex items-start gap-2">
              {msg.type === "question" ? (
                <>
                  <Text className="whitespace-pre-line">
                    <span className="inline-block tab">&#9;</span>
                    {msg.text}
                  </Text>
                  <UserOutlined className="mt-1" />
                </>
              ) : (
                <>
                  <RobotOutlined className="mt-1" />
                  <Text className="whitespace-pre-line">
                    <span className="inline-block tab">&#9;</span>
                    {msg.text}
                  </Text>
                </>
              )}
            </div>
          </div>
        ))}
        {loading && (
          <div className="flex justify-start">
            <Spin />
          </div>
        )}
      </div>

      {/* Error */}
      {error && <Alert type="error" message={error} showIcon className="mx-4 mb-2" />}

      {/* Input */}
      <div className="p-4 border-t border-gray-200 bg-white flex gap-2">
        <Input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onPressEnter={handleSend}
          placeholder="Ask a question..."
        />
        <Button type="primary" onClick={handleSend}>
          Send
        </Button>
      </div>
    </div>
  );
};

export default QAInterface;
