import React, { useState } from 'react';
import { Upload, Button, Typography, Spin, Descriptions } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import type { UploadFile } from 'antd/es/upload/interface';

const { Title } = Typography;

const UploadPage: React.FC = () => {
  const [fileList, setFileList] = useState<UploadFile[]>([]);
  const [loading, setLoading] = useState(false);
  const [uploadedFile, setUploadedFile] = useState<UploadFile | null>(null);

  const handleChange = (info: any) => {
    setLoading(true);

    // Simulate upload delay
    setTimeout(() => {
      const file = info.file.originFileObj;
      setFileList([info.file]);
      setUploadedFile({
        uid: info.file.uid,
        name: info.file.name,
        size: file.size,
        type: file.type,
      });
      setLoading(false);
    }, 1500);
  };

  const getFileExtension = (filename: string) => {
    return filename.split('.').pop();
  };

  return (
    <div style={{ padding: '24px', height: '100%', display: 'flex', flexDirection: 'column' }}>
      <Title level={3}>üìÅ Upload Document</Title>

      <Upload
        beforeUpload={() => false} // prevent automatic upload
        onChange={handleChange}
        fileList={fileList}
        maxCount={1}
        accept=".pdf,.doc,.docx,.txt"
      >
        <Button icon={<UploadOutlined />}>Select File</Button>
      </Upload>

      {loading && (
        <div style={{ marginTop: 24 }}>
          <Spin tip="Uploading..." />
        </div>
      )}

      {!loading && uploadedFile && (
        <Descriptions
          title="File Details"
          bordered
          column={1}
          size="small"
          style={{ marginTop: 24 }}
        >
          <Descriptions.Item label="File Name">{uploadedFile.name}</Descriptions.Item>
          <Descriptions.Item label="Extension">
            {getFileExtension(uploadedFile.name)}
          </Descriptions.Item>
          <Descriptions.Item label="Size">
            {(uploadedFile.size ?? 0 / 1024).toFixed(2)} KB
          </Descriptions.Item>
        </Descriptions>
      )}
    </div>
  );
};

export default UploadPage;
