import React, { useState } from "react";
import { Layout, Row, Col, Typography } from "antd";
import UploadPage from "./components/uploadPage";
import QAInterface from "./components/qaPage";

const { Header, Content, Footer } = Layout;
const { Title } = Typography;

const App: React.FC = () => {
  const [fileList, setFileList] = useState<any[]>([]);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleUploadChange = (info: any) => {
    setFileList(info.fileList.slice(-1));
  };

  const handleQuestionSubmit = () => {
    if (!question.trim()) {
      setError("Please enter a question.");
      return;
    }

    setError("");
    setLoading(true);
    setAnswer("");

    // Simulate response
    setTimeout(() => {
      setLoading(false);
      setAnswer("This is a dummy AI-generated answer.");
    }, 1500);
  };

  return (
    <Layout style={{ minHeight: "100vh" }}>
      <Header style={{ color: "#fff", fontSize: "1.5rem", textAlign: "center" }}>
        AI Document Chat Interface
      </Header>

      <Content style={{ flex: 1, padding: "1rem 2rem", display: "flex" }}>
        <Row
          gutter={24}
          style={{
            width: "100%",
            display: "flex",
            flex: 1,
            height: "100%",
          }}
        >
          {/* Upload Section */}
          <Col span={8} style={{ display: "flex", flexDirection: "column" }}>
            <div
              style={{
                flex: 1,
                background: "#fff",
                padding: "24px",
                borderRadius: "8px",
                boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
                overflow: "auto",
              }}
            >
              <Title level={4}>Upload Document</Title>
              <UploadPage fileList={fileList} onChange={handleUploadChange} />
            </div>
          </Col>

          {/* Chat Section */}
          <Col span={16} style={{ display: "flex", flexDirection: "column" }}>
            <div
              style={{
                flex: 1,
                background: "#fff",
                padding: "24px",
                borderRadius: "8px",
                boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
                overflow: "auto",
              }}
            >
              <Title level={4}>Chat with Document</Title>
              <QAInterface
                loading={loading}
                error={error}
                onSubmit={(question: string) => {
                  // You can add async logic here later
                  return "This is a dummy AI-generated answer.";
                }}
              />
            </div>
          </Col>
        </Row>
      </Content>

      <Footer style={{ textAlign: "center" }}>
        Â© 2025 AI Document Chat Interface
      </Footer>
    </Layout>
  );
};

export default App;
