import React, { useState } from 'react';
import { Upload, Button, Typography, Spin, Descriptions, message } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import type { UploadFile } from 'antd/es/upload/interface';
import axios from 'axios';

const { Title, Text, Paragraph } = Typography;

const UploadPage: React.FC = () => {
  const [fileList, setFileList] = useState<UploadFile[]>([]);
  const [uploading, setUploading] = useState(false);
  const [uploadedFile, setUploadedFile] = useState<{
    uid: string;
    name: string;
    size: number;
    type: string;
    url?: string;
    formattedSize?: string;
  } | null>(null);

  const handleChange = (info: { file: UploadFile; fileList: UploadFile[] }) => {
    setFileList(info.fileList.map(file => ({
      ...file,
      formattedSize: formatFileSize(file.size || 0)
    })));
  };

  const handleRemove = () => {
    setFileList([]);
    setUploadedFile(null);
  };

  const handleUpload = async () => {
    if (fileList.length === 0) {
      message.warning('Please select a file first!');
      return;
    }

    const file = fileList[0];
    const formData = new FormData();
    formData.append('file', file.originFileObj as File);

    setUploading(true);

    try {
      const response = await axios.post('http://localhost:8000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data.status === 'success') {
        message.success('File uploaded successfully!');
        setUploadedFile({
          uid: file.uid,
          name: file.name || 'Unknown',
          size: file.size || 0,
          type: file.type || 'Unknown',
          url: response.data.file_path,
          formattedSize: formatFileSize(file.size || 0)
        });
      }
    } catch (error) {
      message.error('Upload failed. Please try again.');
      console.error('Upload error:', error);
    } finally {
      setUploading(false);
    }
  };

  const getFileExtension = (filename: string) => {
    return filename.split('.').pop();
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <div style={{ padding: '24px', height: '100%', display: 'flex', flexDirection: 'column' }}>
      <Title level={3}>Upload Document</Title>
      
      <Paragraph style={{ fontSize: '16px', color: '#555', maxWidth: '700px' }}>
        This system allows you to upload documents such as PDF, DOCX, or TXT files. After uploading, the document will be processed so that intelligent answers can be generated based on its content. Please upload one file at a time.
      </Paragraph>

      <Upload
        beforeUpload={() => false}
        onChange={handleChange}
        fileList={fileList}
        maxCount={1}
        accept=".pdf,.doc,.docx,.txt"
        disabled={uploading}
        itemRender={(originNode, file) => (
          <div
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: '8px',
              border: '1px solid #f0f0f0',
              borderRadius: '4px',
              marginTop: '8px'
            }}
          >
            <div>
              <span>{file.name}</span>
              <Text type="secondary" style={{ marginLeft: 8 }}>
                {(file as any).formattedSize || formatFileSize(file.size || 0)}
              </Text>
            </div>
            <Button
              type="text"
              danger
              size="small"
              onClick={handleRemove}
              disabled={uploading} // ✅ disable when uploading
            >
              Remove
            </Button>
          </div>
        )}
      >
        <Button icon={<UploadOutlined />}>Select File</Button>
      </Upload>

      {fileList.length > 0 && !uploadedFile && (
        <Button
          type="primary"
          onClick={handleUpload}
          disabled={uploading}
          loading={uploading}
          style={{ marginTop: 16 }}
        >
          {uploading ? 'Uploading...' : 'Start Upload'}
        </Button>
      )}

      {uploading && (
        <div style={{ marginTop: 24, textAlign: 'center' }}>
          <Spin tip="Uploading your file..." size="large">
            <div style={{ padding: '50px', background: 'rgba(0, 0, 0, 0.05)', borderRadius: '4px' }} />
          </Spin>
        </div>
      )}

      {!uploading && uploadedFile && (
        <Descriptions
          title="Uploaded File Details"
          bordered
          column={1}
          size="small"
          style={{ marginTop: 24 }}
        >
          <Descriptions.Item label="File Name">{uploadedFile.name}</Descriptions.Item>
          <Descriptions.Item label="Extension">
            {getFileExtension(uploadedFile.name)}
          </Descriptions.Item>
          <Descriptions.Item label="Type">{uploadedFile.type}</Descriptions.Item>
          <Descriptions.Item label="Size">
            {uploadedFile.formattedSize}
          </Descriptions.Item>
        </Descriptions>
      )}
    </div>
  );
};

export default UploadPage;
