import React from "react";
import { Input, Button, Typography, Spin, Alert } from "antd";
import { SendOutlined } from "@ant-design/icons";

const { Title, Text } = Typography;

interface QAInterfaceProps {
  question: string;
  answer: string;
  loading: boolean;
  error: string;
  onQuestionChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  onSubmit: () => void;
}

const QAInterface: React.FC<QAInterfaceProps> = ({
  question,
  answer,
  loading,
  error,
  onQuestionChange,
  onSubmit,
}) => {
  return (
    <>
      <Title level={4}>2. Ask a Question</Title>
      <Input
        placeholder="Type your question here"
        value={question}
        onChange={onQuestionChange}
        onPressEnter={onSubmit}
      />
      <Button
        type="primary"
        icon={<SendOutlined />}
        onClick={onSubmit}
        style={{ marginTop: "1rem" }}
      >
        Get Answer
      </Button>

      {loading && <Spin tip="Generating answer..." style={{ marginTop: "1rem" }} />}
      {error && <Alert type="error" message={error} style={{ marginTop: "1rem" }} />}
      {answer && (
        <div style={{ marginTop: "1rem" }}>
          <Text strong>Answer:</Text>
          <p>{answer}</p>
        </div>
      )}
    </>
  );
};

export default QAInterface;
