import React, { useState } from "react";
import { Layout, Row, Col, Typography, theme } from "antd";
import UploadPage from "./components/uploadPage";
import QAInterface from "./components/qaPage";

const { Header, Content, Footer } = Layout;
const { Title } = Typography;

const App: React.FC = () => {
  const [fileList, setFileList] = useState<any[]>([]);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleUploadChange = (info: any) => {
    setFileList(info.fileList.slice(-1));
  };

  const handleQuestionSubmit = () => {
    if (!question.trim()) {
      setError("Please enter a question.");
      return;
    }

    setError("");
    setLoading(true);
    setAnswer("");

    // Simulate loading
    setTimeout(() => {
      setLoading(false);
      setAnswer("This is a dummy AI-generated answer.");
    }, 1500);
  };

  return (
    <Layout style={{ minHeight: "100vh" }}>
      <Header style={{ color: "#fff", fontSize: "1.5rem", textAlign: "center" }}>
        AI Document Chat Interface
      </Header>

      <Content style={{ padding: "2rem" }}>
        <Row gutter={32}>
          {/* Upload Column - 1/3 width */}
          <Col xs={24} md={8}>
            <div
              style={{
                background: "#fff",
                padding: "24px",
                borderRadius: "8px",
                boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
              }}
            >
              <Title level={4}>Upload Document</Title>
              <UploadPage fileList={fileList} onChange={handleUploadChange} />
            </div>
          </Col>

          {/* Chat Column - 2/3 width */}
          <Col xs={24} md={16}>
            <div
              style={{
                background: "#fff",
                padding: "24px",
                borderRadius: "8px",
                boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
              }}
            >
              <Title level={4}>Chat with Document</Title>
              <QAInterface
                question={question}
                answer={answer}
                loading={loading}
                error={error}
                onQuestionChange={(e) => setQuestion(e.target.value)}
                onSubmit={handleQuestionSubmit}
              />
            </div>
          </Col>
        </Row>
      </Content>

      <Footer style={{ textAlign: "center" }}>
        Â© 2025 AI Document Chat Interface
      </Footer>
    </Layout>
  );
};

export default App;
