// components/QAInterface.tsx
import React, { useState } from "react";
import { Input, Button, Spin, Alert } from "antd";

interface Message {
  type: "question" | "answer";
  text: string;
}

interface Props {
  loading: boolean;
  error: string;
  onSubmit: (question: string) => string;
}

const QAInterface: React.FC<Props> = ({ loading, error, onSubmit }) => {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState<Message[]>([]);

  const handleSend = () => {
    if (!input.trim()) return;

    const userMessage: Message = { type: "question", text: input };
    setMessages((prev) => [...prev, userMessage]);
    setInput("");

    // Simulate AI response
    const response = onSubmit(input);
    setMessages((prev) => [...prev, { type: "answer", text: response }]);
  };

  return (
    <div className="flex flex-col h-full">
      {/* Chat history */}
      <div className="flex-1 overflow-y-auto space-y-4 pr-2">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`max-w-[75%] px-4 py-2 rounded-xl ${
              msg.type === "question"
                ? "bg-blue-100 self-end text-right"
                : "bg-gray-100 self-start text-left"
            }`}
          >
            {msg.text}
          </div>
        ))}
        {loading && (
          <div className="self-start">
            <Spin />
          </div>
        )}
      </div>

      {/* Error */}
      {error && <Alert type="error" message={error} showIcon className="mb-2" />}

      {/* Input */}
      <div className="flex gap-2 pt-4">
        <Input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onPressEnter={handleSend}
          placeholder="Ask a question..."
        />
        <Button type="primary" onClick={handleSend}>
          Send
        </Button>
      </div>
    </div>
  );
};

export default QAInterface;
