// components/QAInterface.tsx
import React, { useState } from "react";
import { Input, Button, Spin, Alert, Avatar, Typography } from "antd";
import { UserOutlined, RobotOutlined } from "@ant-design/icons";

const { Text } = Typography;

interface Message {
  type: "question" | "answer";
  text: string;
}

interface Props {
  loading: boolean;
  error: string;
  onSubmit: (question: string) => string;
}

const QAInterface: React.FC<Props> = ({ loading, error, onSubmit }) => {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState<Message[]>([]);

  const handleSend = () => {
    if (!input.trim()) return;

    const userMessage: Message = { type: "question", text: input };
    setMessages((prev) => [...prev, userMessage]);
    setInput("");

    const response = onSubmit(input);
    setTimeout(() => {
      setMessages((prev) => [...prev, { type: "answer", text: response }]);
    }, 500);
  };

  return (
    <div
      className="flex flex-col h-full bg-gray-50 rounded-lg"
      style={{ border: "1px solid #f0f0f0" }}
    >
      {/* Chat history */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`flex ${
              msg.type === "question" ? "justify-end" : "justify-start"
            }`}
          >
            <div className="flex gap-2 max-w-[75%]">
              {msg.type === "answer" && (
                <Avatar icon={<RobotOutlined />} size="small" />
              )}
              <div
                className={`px-4 py-2 rounded-xl shadow-sm ${
                  msg.type === "question"
                    ? "bg-blue-100 text-right"
                    : "bg-white text-left"
                }`}
              >
                <Text>{msg.text}</Text>
              </div>
              {msg.type === "question" && (
                <Avatar icon={<UserOutlined />} size="small" />
              )}
            </div>
          </div>
        ))}
        {loading && (
          <div className="flex justify-start">
            <Spin />
          </div>
        )}
      </div>

      {/* Error */}
      {error && <Alert type="error" message={error} showIcon className="mx-4 mb-2" />}

      {/* Input */}
      <div className="p-4 border-t border-gray-200 bg-white flex gap-2">
        <Input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onPressEnter={handleSend}
          placeholder="Ask a question..."
        />
        <Button type="primary" onClick={handleSend}>
          Send
        </Button>
      </div>
    </div>
  );
};

export default QAInterface;
