import React, { useState } from "react";
import { Layout, Row, Col, Typography } from "antd";
import UploadPage from "./components/UploadPage";
import QAInterface from "./components/QAInterface";

const { Header, Content, Footer } = Layout;
const { Title } = Typography;

const App: React.FC = () => {
  const [fileList, setFileList] = useState<any[]>([]);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleUploadChange = (info: any) => {
    setFileList(info.fileList.slice(-1));
  };

  const handleQuestionSubmit = () => {
    if (!question.trim()) {
      setError("Please enter a question.");
      return;
    }

    setError("");
    setLoading(true);
    setAnswer("");

    setTimeout(() => {
      setLoading(false);
      setAnswer("This is a dummy AI-generated answer.");
    }, 1500);
  };

  return (
    <Layout style={{ minHeight: "100vh" }}>
      <Header style={{ color: "#fff", fontSize: "1.5rem" }}>
        AI Document Chat Interface
      </Header>
      <Content style={{ padding: "2rem" }}>
        <Row gutter={24}>
          <Col span={8}>
            <Title level={4}>Upload Document</Title>
            <UploadPage fileList={fileList} onChange={handleUploadChange} />
          </Col>
          <Col span={16}>
            <Title level={4}>Chat with Document</Title>
            <QAInterface
              question={question}
              answer={answer}
              loading={loading}
              error={error}
              onQuestionChange={(e) => setQuestion(e.target.value)}
              onSubmit={handleQuestionSubmit}
            />
          </Col>
        </Row>
      </Content>
      <Footer style={{ textAlign: "center" }}>Â© 2025 AI Document Chat</Footer>
    </Layout>
  );
};

export default App;
