import React, { useState } from 'react';
import { Input, Typography } from 'antd';
import { UserOutlined, RobotOutlined, AntDesignOutlined } from '@ant-design/icons';

const { Text } = Typography;

const QAPage: React.FC = () => {
  const [inputValue, setInputValue] = useState('');
  const [messages, setMessages] = useState<{ type: 'question' | 'answer'; content: string }[]>([]);

  const handleSend = () => {
    if (!inputValue.trim()) return;

    setMessages(prev => [
      ...prev,
      { type: 'question', content: inputValue },
      { type: 'answer', content: 'This is a dummy AI-generated answer.' },
    ]);
    setInputValue('');
  };

  return (
    <div style={{ padding: '24px', height: '100%', display: 'flex', flexDirection: 'column' }}>
      <Typography.Title level={3}>üêú Chat with Document</Typography.Title>

      <div style={{ flex: 1, overflowY: 'auto', marginBottom: '16px' }}>
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: msg.type === 'question' ? 'flex-start' : 'center',
              marginBottom: 24,
            }}
          >
            <div
              style={{
                border: '1px solid #d9d9d9',
                borderRadius: 8,
                padding: '10px 14px',
                backgroundColor: msg.type === 'question' ? '#f5f5f5' : '#e6f7ff',
                width: '100%',
                maxWidth: '80%',
              }}
            >
              <Text>
                {msg.type === 'question' ? <UserOutlined /> : <RobotOutlined />}
                <span style={{ marginLeft: 8 }}>{msg.content}</span>
              </Text>
            </div>
          </div>
        ))}
      </div>

      {/* Input and icon in horizontal flex container */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
        <Input
          value={inputValue}
          onChange={e => setInputValue(e.target.value)}
          onPressEnter={handleSend}
          placeholder="Ask a question..."
          style={{ flex: 1 }}
        />
        <AntDesignOutlined
          onClick={handleSend}
          style={{
            fontSize: 24,
            color: '#1890ff',
            cursor: 'pointer',
            transition: 'transform 0.2s',
          }}
          onMouseEnter={e => (e.currentTarget.style.transform = 'scale(1.2)')}
          onMouseLeave={e => (e.currentTarget.style.transform = 'scale(1)')}
        />
      </div>
    </div>
  );
};

export default QAPage;
